import React, { Component } from 'react'

export class NewsItem extends Component {
    
  render() {
    const  {title, description,imageUrl,newsUrl,author, date,source} = this.props;

    return (
      <div className ='container my-3'>
        <div class="card" style= {{width: "18rem"}}>
      <img src={imageUrl ? imageUrl : 'https://scitechdaily.com/images/Magnetar-FRB-Concept.jpg' } class="card-img-top" alt="..."/>
      
     <div class="card-body">
    <h5 class="card-title">{title}<span class="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
    {source}
    <span class="visually-hidden">unread messages</span>
  </span>
  </h5>
     <p class="card-text">{description}</p>
     <p class="card-text"><small class="text-body-secondary">By {author ? author : 'unknown'} on {new Date(date).toGMTString()}</small></p>
     <a href={newsUrl} target='_blank' class="btn btn-sm btn-dark">Read more</a>
  </div>
</div>
      </div>
    )
  }
}

export default NewsItem
