import React, { Component } from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';


export class News extends Component {

    constructor(){
        console.log("hello I am constructor from News Component")
        super();
        this.state ={
            articles : [],
            loading: false,
            page: 1,
            
    }
}
    
    async componentDidMount(){
        let url =`https://newsapi.org/v2/top-headlines?country=in&apiKey=ce77274e0d7b49c1a1a2184fb491d8a8&page=1&pageSize=${this.props.pageSize}`
        let data = await  fetch(url);
        this.setState({loading:true});
        //console.log(data);
        let parsedData = await data.json();
        //console.log(parsedData);
        this.setState ({
            articles : parsedData.articles,
            totalResults : parsedData.totalResults,
            loading: false,
        });
    }
   
    handlePrevClick = async() =>{
    console.log('prev');
    console.log('next');
    let url =`https://newsapi.org/v2/top-headlines?country=in&apiKey=ce77274e0d7b49c1a1a2184fb491d8a8&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`
    let data = await  fetch(url);
    this.setState({loading:true});
    //console.log(data);
    let parsedData = await data.json();
    //console.log(parsedData);
    this.setState ({
        articles : parsedData.articles,
        page : this.state.page - 1 ,
        loading:false,
    });

    }
    handleNextClick = async() =>{
        if(this.state.page + 1 >Math.ceil(this.state.totalResults/this.props.pageSize)){

        }
        else{
        console.log('next');
        let url =`https://newsapi.org/v2/top-headlines?country=in&apiKey=ce77274e0d7b49c1a1a2184fb491d8a8&page=${this.state.page +1}&pageSize=${this.props.pageSize}`
        let data = await  fetch(url);
        this.setState({loading:true});
        //console.log(data);
        let parsedData = await data.json();
        //console.log(parsedData);
        this.setState ({
            articles : parsedData.articles,
            page : this.state.page +1 ,
            loading:false,
        });
    }
    }


  render() {
    return (
      <div className ='container my-2'>
        <div className='text-center'><h1>NewsMonkey Top Headlines</h1></div>
        {this.state.loading && <Spinner/>}
    
        <div className= 'row' >
            {this.state.articles.map((element) => {
                return <div className ='col md-3' key ={element.url}>
                <NewsItem 
                title = {element.title ? element.title.slice(0,45) : ""} 
                description = {element.description ? element.description.slice(0,88) : ""} 
                imageUrl ={element.urlToImage}
                newsUrl = {element.url}
                />   
                </div>
                 
            })
            }

        </div>
        <div className ='container d-flex justify-content-between'>
        <button disabled={this.state.page<=1} onClick={this.handlePrevClick} type="button" class="btn btn-dark">&larr; Previous</button>
        <button disabled={this.state.page + 1 >Math.ceil(this.state.totalResults/this.props.pageSize)} onClick={this.handleNextClick} type="button" class="btn btn-dark">Next &rarr;</button>
        </div>

      </div>
    )
  }
}

export default News;
