import React, { Component } from 'react';
import NewsItem from './NewsItem';


export class News extends Component {

    constructor(){
        console.log("hello I am constructor from News Component")
        super();
        this.state ={
            articles : [],
            page: 1,
            loading : false,
        }
    }
    
    async componentDidMount(){
        let url ='https://newsapi.org/v2/top-headlines?country=in&apiKey=ce77274e0d7b49c1a1a2184fb491d8a8&page=1'
        let data = await  fetch(url);
        //console.log(data);
        let parsedData = await data.json();
        //console.log(parsedData);
        this.setState ({
            articles : parsedData.articles,
        });
    }
   
    handlePrevClick = async() =>{
    console.log('next')
    }
    handleNextClick = async() =>{
        console.log('next')
    }


  render() {
    return (
      <div className ='container my-2'>
        <h1>NewsMonkey Top Headlines</h1>
        <div className= 'row' >
            {this.state.articles.map((element) => {
                return <div className ='col md-3' key ={element.url}>
                <NewsItem 
                title = {element.title ? element.title.slice(0,45) : ""} 
                description = {element.description ? element.description.slice(0,88) : ""} 
                imageUrl ={element.urlToImage}
                newsUrl = {element.url}
                />   
                </div>
                 
            })
            }

        </div>
        <div className ='container d-flex justify-content-between'>
        <button disabled={this.state.page<=1} onClick={handlePrevClick} type="button" class="btn btn-dark">&larr; Previous</button>
        <button onClick={handleNextClick} type="button" class="btn btn-dark">Next &rarr;</button>
        </div>

      </div>
    )
  }
}

export default News;
